// Gelen SKU klasörlerini al
const skuFolders = $input.all();

// Google Drive2'den part number klasörlerini al (bunlar parent klasörler)
const partNumberFolders = $('Google Drive2').all();

// Form'dan gelen opsiyonel klasör adını al
const formData = $('Code').first().json;
const filterFolder = formData.folderName;

// Her SKU için gerekli bilgileri hazırla
let results = skuFolders.map(item => {
  const skuFolder = item.json;

  // Klasör adından bilgileri çıkar
  const folderName = skuFolder.name;

  // parentPartNumber: ilk rakamlar (örn: 12602221)
  const parentPartNumber = folderName.match(/^\d+/)?.[0] || folderName;

  // Parent klasör ID'sini bul (Google Drive2'den)
  const parentFolder = partNumberFolders.find(f => f.json.name === parentPartNumber);
  const parentFolderId = parentFolder ? parentFolder.json.id : null;

  return {
    json: {
      skuFolderId: skuFolder.id,
      skuFolderName: folderName,
      partNumber: folderName,
      parentPartNumber: parentPartNumber,
      parentFolderId: parentFolderId,
      sku: folderName,
      modelNumber: folderName
    }
  };
});

// Eğer opsiyonel klasör adı girildiyse, sadece o klasörü filtrele
if (filterFolder && filterFolder.trim() !== '') {
  results = results.filter(item => 
    item.json.parentPartNumber === filterFolder.trim() || 
    item.json.partNumber === filterFolder.trim()
  );
}

return results;

const mammoth = require('mammoth');

// İndirilen dosyanın binary verisini al
const binaryData = $input.first().binary.data;
const buffer = Buffer.from(binaryData.data, 'base64');

// Önceki node'lardan SKU bilgilerini al
const skuData = $('Code1').first().json;

// Mammoth ile docx'i text'e çevir
const result = await mammoth.extractRawText({ buffer: buffer });
const text = result.value;

// Text'i bölümlere ayır
let title = '';
let bulletPoints = '';
let description = '';
let genericKeywords = '';

// Bölümleri regex ile ayır
const titleMatch = text.match(/TITLE:\s*([\s\S]*?)(?=BULLET POINTS:|$)/i);
const bulletMatch = text.match(/BULLET POINTS:\s*([\s\S]*?)(?=DESCRIPTION:|$)/i);
const descMatch = text.match(/DESCRIPTION:\s*([\s\S]*?)(?=GENERIC KEYWORDS:|$)/i);
const keywordsMatch = text.match(/GENERIC KEYWORDS:\s*([\s\S]*?)$/i);

if (titleMatch) title = titleMatch[1].trim();
if (bulletMatch) bulletPoints = bulletMatch[1].trim();
if (descMatch) description = descMatch[1].trim();
if (keywordsMatch) genericKeywords = keywordsMatch[1].trim();

return [{
  json: {
    // SKU bilgileri
    skuFolderId: skuData.skuFolderId,
    partNumber: skuData.partNumber,
    parentPartNumber: skuData.parentPartNumber,
    parentFolderId: skuData.parentFolderId,
    sku: skuData.sku,
    modelNumber: skuData.modelNumber,
    // Content bilgileri
    title: title,
    bulletPoints: bulletPoints,
    description: description,
    genericKeywords: genericKeywords,
    ebayDescription: bulletPoints + '\n\n' + description
  }
}];

// Merge'den gelen tüm görselleri al (Filter2 + Filter3 birleşik)
const allInputImages = $input.all();

// Önceki node'lardan bilgileri al
const contentData = $('Code2').first().json;

// Görselleri source'a göre ayır (ilk 6 parent'tan, sonraki SKU'dan)
// Filter3 (parent) 6 item, Filter2 (sku) 1 item gönderdi
// Merge append yaptığı için sıralama: önce Input1 (Filter3), sonra Input2 (Filter2)
const parentImages = [];
const skuImages = [];

// Append modunda önce Filter3 (parent), sonra Filter2 (sku) gelir
// Filter3 = 6 items, Filter2 = 1 item varsayıyoruz
const filter3Count = $('Filter3').all().length;

allInputImages.forEach((item, index) => {
  if (index < filter3Count) {
    parentImages.push({
      id: item.json.id,
      name: item.json.name,
      source: 'parent'
    });
  } else {
    skuImages.push({
      id: item.json.id,
      name: item.json.name,
      source: 'sku'
    });
  }
});

// Tüm görselleri birleştir
const allImages = [...parentImages, ...skuImages];

// Duplicate isimleri kaldır (aynı isimde varsa parent'ı tercih et)
const uniqueImages = [];
const seenNames = new Set();
for (const img of allImages) {
  const lowerName = img.name.toLowerCase();
  if (!seenNames.has(lowerName)) {
    seenNames.add(lowerName);
    uniqueImages.push(img);
  }
}

// Görselleri sırala
const sortedImages = uniqueImages.sort((a, b) => {
  const aName = a.name.toLowerCase();
  const bName = b.name.toLowerCase();

  const aNumMatch = aName.match(/^(\d+)/);
  const bNumMatch = bName.match(/^(\d+)/);
  const aStdMatch = aName.match(/^std(\d+)/i);
  const bStdMatch = bName.match(/^std(\d+)/i);

  const aIsNum = aNumMatch && !aStdMatch;
  const bIsNum = bNumMatch && !bStdMatch;

  // Rakamla başlayanlar önce
  if (aIsNum && !bIsNum) return -1;
  if (!aIsNum && bIsNum) return 1;

  if (aIsNum && bIsNum) {
    return parseInt(aNumMatch[1]) - parseInt(bNumMatch[1]);
  }

  // std olanlar sonra
  if (aStdMatch && !bStdMatch) return -1;
  if (!aStdMatch && bStdMatch) return 1;

  if (aStdMatch && bStdMatch) {
    return parseInt(aStdMatch[1]) - parseInt(bStdMatch[1]);
  }

  return aName.localeCompare(bName);
});

// Google Drive public link oluştur
const imageUrls = sortedImages.map(img =>
  `https://drive.google.com/uc?export=view&id=${img.id}`
);

