// Price verilerini al
const priceRows = $input.all();

// Code3'ten ürün verisini al
const productData = $('Code3').first().json;

// parentPartNumber'a göre fiyatı bul
const parentPartNumber = productData.parentPartNumber;

let price = '';
for (const row of priceRows) {
  const rowData = row.json;

  if (rowData.part_number && rowData.part_number.toString() === parentPartNumber) {
    price = rowData.price;
    break;
  }
}

return [{
  json: {
    ...productData,
    price: price || '0'
  }
}];

// Category verilerini al
const categoryRows = $input.all();

// Code4'ten ürün verisini al
const productData = $('Code4').first().json;

const parentPartNumber = productData.parentPartNumber;

// Part number'a göre kategori belirle
// 126 ile başlayanlar wiper blade, 182 ile başlayanlar power window switch
let categoryName = '';
if (parentPartNumber.startsWith('126')) {
  categoryName = 'wiper blade';
} else if (parentPartNumber.startsWith('182')) {
  categoryName = 'power window switch';
} else {
  categoryName = 'wiper blade'; // varsayılan
}

let categoryId = '';
for (const row of categoryRows) {
  const rowData = row.json;

  if (rowData.category && rowData.category.toLowerCase() === categoryName.toLowerCase()) {
    categoryId = rowData['ebay category id'];
    break;
  }
}

return [{
  json: {
    ...productData,
    categoryName: categoryName,
    categoryId: categoryId || ''
  }
}];

// OpenAI'dan gelen eBay title
const openAIResponse = $input.first().json;
const ebayTitle = openAIResponse.message.content;

// Code5'ten ürün verisini al
const productData = $('Code5').first().json;

return [{
  json: {
    ...productData,
    ebayTitle: ebayTitle
  }
}];

// Code6'dan ürün verisini al
const productData = $input.first().json;

// Fotoğrafları | ile birleştir (eBay formatı)
const picUrls = [];
for (let i = 1; i <= 24; i++) {
  const url = productData[`picURL${i}`];
  if (url && url !== '') {
    picUrls.push(url);
  }
}
const itemPhotoURL = picUrls.join('|');

