  "Item description": description,
  "Format": "FixedPrice",
  "Duration": "GTC"
};

return [{
  json: csvRow
}];

// Code1'den gelen SKU bilgilerini al
const skuData = $('Code1').first().json;

// _exported.txt arama sonuçlarını al
const exportedFiles = $input.all();

// Eğer _exported.txt bulunduysa, bu SKU'yu atla (boş döndür)
let hasExported = false;
for (const file of exportedFiles) {
  if (file.json.name && file.json.name.toLowerCase() === '_exported.txt') {
    hasExported = true;
    break;
  }
}

if (hasExported) {
  // Zaten export edilmiş, atla
  return [];
}

// Export edilmemiş, devam et
return [{
  json: skuData
}];

// Code7'den gelen veriyi al
const data = $input.first().json;

// Description'ı HTML formatına çevir ve newline'ları temizle
const descriptionText = (data['Item description'] || data['Description'] || data.ebayDescription || '')
  .replace(/\r\n/g, ' ')
  .replace(/\n/g, ' ')
  .replace(/\r/g, ' ');
const description = `<p>${descriptionText}</p>`;

// Fotoğrafları al ve Google Drive URL'ini dönüştür
let photoUrls = data['Item photo URL'] || '';

// URL'leri dönüştür
if (photoUrls) {
  const urls = photoUrls.split('|').map(url => {
    const idMatch = url.match(/id=([a-zA-Z0-9_-]+)/);
    if (idMatch) {
      return `https://lh3.googleusercontent.com/d/${idMatch[1]}`;
    }
    return url;
  });
  photoUrls = urls.join('|');
}

// CSV satırları (COMMA-separated)
const rows = [];

// Satır 1
rows.push('#INFO,Version=0.0.2,Template= eBay-draft-listings-template_US,,,,,,,,');

// Satır 2
rows.push('#INFO Action and Category ID are required fields. 1) Set Action to Draft 2) Please find the category ID for your listings here: https://pages.ebay.com/sellerinformation/news/categorychanges.html,,,,,,,,,,');

// Satır 3
rows.push('"#INFO After you\'ve successfully uploaded your draft from the Seller Hub Reports tab, complete your drafts to active listings here: https://www.ebay.com/sh/lst/drafts",,,,,,,,,,');

// Satır 4
