// Satır 6 - Data
const escapeCSV = (val) => {
  if (!val) return '';
  const str = String(val);
  if (str.includes(',') || str.includes('"') || str.includes('\n')) {
    return '"' + str.replace(/"/g, '""') + '"';
  }
  return str;
};

const sku = data['Custom label (SKU)'] || data.sku || '';
const categoryId = data['Category ID'] || data.categoryId || '';
const title = data['Title'] || data.ebayTitle || '';
const price = data['Price'] || data['Start price'] || data.price || '';
const quantity = data['Quantity'] || '0';
const conditionId = 'NEW';
const format = data['Format'] || 'FixedPrice';

const dataRow = [
  'Draft',
  escapeCSV(sku),
  escapeCSV(categoryId),
  escapeCSV(title),
  '', // UPC
  escapeCSV(price),
  escapeCSV(quantity),
  escapeCSV(photoUrls),
  conditionId,
  escapeCSV(description),
  format
].join(',');

rows.push(dataRow);

// Tüm satırları birleştir
const fileContent = rows.join('\n');

// Dosya adı
const brandName = $('Code').first().json.brandName;
const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, 19);
const fileName = `${brandName}_${timestamp}.csv`;

